package coursera.bio;

import static coursera.bio.StringFromGenomPath.reconstruct;
import static org.junit.Assert.assertEquals;

import java.util.Arrays;
import java.util.List;

import org.junit.Test;

public class StringFromGenomPathTest {

	@Test
	public void reconstruct() {
		List<String> input = Arrays.asList("ACCGA", "CCGAA", "CGAAG", "GAAGC",
				"AAGCT");
		assertEquals("ACCGAAGCT", reconstruct.apply(input));
	}

	@Test
	public void reconstructExtraDataset() throws Exception {
		List<String> lines = FileUtil.loadFile("GenomePathString.txt");
		int length = lines.size();
		List<String> kmers = lines.subList(1, length - 2);
		String expected = lines.get(length - 1);
		assertEquals(expected, reconstruct.apply(kmers));
	}

	@Test
	public void recontructDataset() throws Exception {
		List<String> kmers = FileUtil.loadFile("dataset_198_3.txt");
		String expected = "";
		assertEquals(expected, reconstruct.apply(kmers));
	}
}